Param (
  [string]$location
)
Write-Host "`n[CDAF_Desktop_Certificate.ps1] ---------- start ----------"
Write-Host "`n[CDAF_Desktop_Certificate.ps1] Requires elevated privilages"

if (! $location) {
    $location = 'CurrentUser'
}

Write-Host "`n[CDAF_Desktop_Certificate.ps1] Import the certificate from encoded content"
$EncodedText = ''
$ByteArray = [System.Convert]::FromBase64String($EncodedText)

# Create an object for the certificate
$pfx = new-object System.Security.Cryptography.X509Certificates.X509Certificate2
$pfx.import($ByteArray , 'password', 'Exportable,PersistKeySet')

Write-Host "`n[CDAF_Desktop_Certificate.ps1] Access the store (My/$location)"
$store = new-object System.Security.Cryptography.X509Certificates.X509Store('My', $location)
$store.open('MaxAllowed')
$store.add($pfx)
$store.close()

Write-Host "`n[CDAF_Desktop_Certificate.ps1] Import complete"
Get-ChildItem -path cert:\$location\My

Write-Host "`n[CDAF_Desktop_Certificate.ps1] ---------- stop -----------"
$error.clear()
exit 0